import React, { useState } from "react";
import { Box, Typography, useTheme, useMediaQuery, TextField, Button, Alert, Collapse, Card } from "@mui/material";
import toast from "react-hot-toast";
import axios from "axios";

const OCR = () => {
  const theme = useTheme();
  // Media
  const isNotMobile = useMediaQuery("(min-width: 1000px)");
  // States
  const [imagePath, setImagePath] = useState("");
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const performOCR = async () => {
    try {
      const { data } = await axios.post("/api/v1/tesseract/ocr", { imagePath });
      console.log(data);
      setResults(data);
    } catch (err) {
      console.error(err);
      setError("Error processing OCR.");
    }
  };

  return (
    <Box
      width={isNotMobile ? "40%" : "80%"}
      p={"2rem"}
      m={"2rem auto"}
      borderRadius={5}
      sx={{ boxShadow: 5 }}
      backgroundColor={theme.palette.background.default}
    >
      <Collapse in={error}>
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      </Collapse>
      <Typography variant="h3">OCR (Optical Character Recognition)</Typography>
      <TextField
        placeholder="Path to image file"
        type="text"
        required
        margin="normal"
        fullWidth
        value={imagePath}
        onChange={(e) => setImagePath(e.target.value)}
      />
      <Button
        onClick={performOCR}
        fullWidth
        variant="contained"
        size="large"
        sx={{
          backgroundColor: theme.palette.background.alt,
          color: "white",
          mt: 2,
          "&:hover": { backgroundColor: theme.palette.background.basic },
        }}
      >
        Perform OCR
      </Button>
      {results ? (
        <Card sx={{ mt: 4, border: 1, boxShadow: 0, borderRadius: 5, borderColor: "natural.medium", bgcolor: "background.default" }}>
          <Typography p={2}>{results}</Typography>
        </Card>
      ) : (
        <Card sx={{ mt: 4, border: 1, boxShadow: 0, borderRadius: 5, borderColor: "natural.medium", bgcolor: "background.default" }}>
          <Typography variant="h6" color="natural.main" sx={{ textAlign: "center", verticalAlign: "middle", lineHeight: "450px", fontStyle: "italic" }}>
            Recognized text will be displayed here
          </Typography>
        </Card>
      )}
    </Box>
  );
};

export default OCR;
