import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import toast from "react-hot-toast";
import axios from "axios";
import {Box,Typography,useTheme,useMediaQuery,TextField,Button,Alert,Collapse} from "@mui/material";

const Summary = () => {
  const theme = useTheme();
  const navigate = useNavigate();
  //media
  const isNotMobile = useMediaQuery("(min-width: 1000px)");
  // states
  const [text, settext] = useState("");
  const [summary, setSummary] = useState("");
  const [error, setError] = useState("");

  //Summary ctrl
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/v1/openai/summary", { text});
    } catch (err) {
      console.log(err);
      if (err.response.data.error) {
        setError(err.response.data.error);
      } else if (err.message) {
        setError(err.message);
      }
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };
  return (
    <Box
      width={isNotMobile ? "40%" : "80%"}
      p={"2rem"}
      m={"2rem auto"}
      borderRadius={5}
      sx={{ boxShadow: 5 }}
      backgroundColor={theme.palette.background.default}
    >
      <Collapse in={error}>
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      </Collapse>
      <form onSubmit={handleSubmit}>
        <Typography variant="h3">Summarize</Typography>

        <TextField
          label="summary"
          type="summary"
          required
          margin="normal"
          fullWidth
          value={text}
          onChange={(e) => {
            settext(e.target.value);
          }}
        />
        
        <Button type='submit' fullWidth variant='contained' size='large' sx={{backgroundColor: theme.palette.background.alt, color:'white', mt:2, '&:hover':{backgroundColor:theme.palette.background.basic} }}> 
          Submit
        </Button>

        <Typography mt={2}>
            Not the right tool ? <Link to="/">Go Back</Link>
        </Typography>
      </form>
    </Box>
  );
};

export default Summary;
