// OcrComponent.js
import React, { useEffect, useState } from 'react';
import { createWorker } from 'tesseract.js';
import { Box, Typography, useTheme, useMediaQuery, Alert, Collapse, Card } from '@mui/material';

const OcrComponent = () => {
  const [ocrText, setOcrText] = useState('');

  useEffect(() => {
    const startOcr = async () => {
      const worker = await createWorker();
      try {
        await worker.loadLanguage('ind');
        await worker.initialize('ind');
        await worker.setParameters({});

        const imagePath = 'ktp.jpeg';
        const image = await Jimp.read(imagePath);
        image.rgba(false);
        image.greyscale();
        await image.write('edited_' + imagePath);

        const data = await worker.recognize('edited_' + imagePath);
        const text = data.data.text;
        setOcrText(text);
      } finally {
        await worker.terminate();
      }
    };

    startOcr();
  }, []);

  return (
    <Card>
      <Box p={2}>
        <Typography variant="h5" gutterBottom>
          OCR Result
        </Typography>
        <Collapse in={ocrText.trim().length > 0}>
          <Alert severity="info">{ocrText}</Alert>
        </Collapse>
      </Box>
    </Card>
  );
};

export default OcrComponent;
